// BÆ°á»›c 1: Láº¥y raw content tá»« message
const raw = $input.first().json.message.content;

// BÆ°á»›c 2: Loáº¡i bá» náº¿u bá»‹ bá»c bá»Ÿi ```json markdown
const clean = raw.replace(/```json|```/g, '').trim();

// BÆ°á»›c 3: Parse JSON
let parsed;
try {
  parsed = JSON.parse(clean);
} catch (e) {
  throw new Error("âŒ KhÃ´ng thá»ƒ parse JSON tá»« OpenAI. Ná»™i dung:\n" + raw);
}

// BÆ°á»›c 4: Láº¥y scores ra vÃ  tÃ­nh Ä‘iá»ƒm trung bÃ¬nh
const scores = parsed.scores;
const values = Object.values(scores);

const avg = values.reduce((a, b) => a + b, 0) / values.length;

// LÃ m trÃ²n Ä‘Ãºng chuáº©n IELTS: chá»‰ .0 hoáº·c .5
let rounded;
const decimal = avg - Math.floor(avg);

if (decimal < 0.25) {
  rounded = Math.floor(avg);
} else if (decimal < 0.75) {
  rounded = Math.floor(avg) + 0.5;
} else {
  rounded = Math.ceil(avg);
}

// Tráº£ káº¿t quáº£
return [
  {
    json: {
      scores,
      average_band: rounded
    }
  }
];
